mapper.update:
update t_banner 
	set update_Date = now()
		,image_url = #{imageUrl}
		,jump_url = #{jumpUrl}
		,status = #{status}
where banner_id = #{bannerId}

mapper.select:
select 
		banner_id
		,image_url
		,jump_url
		,status
		,create_date
		,update_date
 from
t_banner


	image_url = #{imageUrl}


	and jump_url = #{jumpUrl}


	and status = #{status}


	and create_date = #{createDate}


	and update_date = #{updateDate}


order by banner_id ,create_date desc limit 1;

