mapper.update:
update t_contact 
	set update_Date = now()
		,username = #{username}
		,mobileNum = #{mobileNum}
		,email = #{email}
		,vehicle_brand = #{vehicleBrand}
		,type = #{type}
		,contents = #{contents}
where contact_id = #{contactId}

mapper.select:
select 
		contact_id
		,username
		,mobileNum
		,email
		,vehicle_brand
		,type
		,contents
		,create_date
		,update_date
 from
t_contact


	username = #{username}


	and mobileNum = #{mobileNum}


	and email = #{email}


	and vehicle_brand = #{vehicleBrand}


	and type = #{type}


	and contents = #{contents}


	and create_date = #{createDate}


	and update_date = #{updateDate}


order by contact_id ,create_date desc limit 1;

