mapper.update:
update t_driver 
	set update_Date = now()
		,driver_name = #{driverName}
		,cover_image = #{coverImage}
		,content = #{content}
		,status = #{status}
where driver_id = #{driverId}

mapper.select:
select 
		driver_id
		,driver_name
		,cover_image
		,content
		,status
		,create_date
		,update_date
 from
t_driver


	driver_name = #{driverName}


	and cover_image = #{coverImage}


	and content = #{content}


	and status = #{status}


	and create_date = #{createDate}


	and update_date = #{updateDate}


order by driver_id ,create_date desc limit 1;

