mapper.update:
update t_news 
	set update_Date = now()
		,title = #{title}
		,cover_image = #{coverImage}
		,`describe` = #{describe}
		,status = #{status}
		,content = #{content}
		,news_type_id = #{newsTypeId}
where news_id = #{newsId}

mapper.select:
select 
		news_id
		,title
		,cover_image
		,`describe`
		,status
		,content
		,create_date
		,update_date
		,news_type_id
 from
t_news


	title = #{title}


	and cover_image = #{coverImage}


	and `describe` = #{describe}


	and status = #{status}


	and content = #{content}


	and create_date = #{createDate}


	and update_date = #{updateDate}


	and news_type_id = #{newsTypeId}


order by news_id ,create_date desc limit 1;

