mapper.update:
update t_news_type 
	set update_Date = now()
		,typeName = #{typeName}
where news_type_id = #{newsTypeId}

mapper.select:
select 
		news_type_id
		,typeName
		,create_date
		,update_date
 from
t_news_type


	typeName = #{typeName}


	and create_date = #{createDate}


	and update_date = #{updateDate}


order by news_type_id ,create_date desc limit 1;

