mapper.update:
update t_product 
	set update_Date = now()
		,title = #{title}
		,cover_image = #{coverImage}
		,`describe` = #{describe}
		,content = #{content}
		,status = #{status}
		,sort = #{sort}
		,product_type_id = #{productTypeId}
where product_id = #{productId}

mapper.select:
select 
		product_id
		,title
		,cover_image
		,describe
		,`describe`
		,status
		,product_type_id
		,sort
		,create_date
		,update_date
 from
t_product


	title = #{title}


	and cover_image = #{coverImage}


	and describe = #{describe}


	and content = #{content}


	and status = #{status}


	and product_type_id = #{productTypeId}


	and create_date = #{createDate}


	and update_date = #{updateDate}


order by sort ,create_date desc limit 1;

