mapper.update:
update t_product_type 
	set update_Date = now()
		,typeName = #{typeName}
where product_type_id = #{productTypeId}

mapper.select:
select 
		product_type_id
		,typeName
		,create_date
		,update_date
 from
t_product_type


	typeName = #{typeName}


	and create_date = #{createDate}


	and update_date = #{updateDate}


order by product_type_id ,create_date desc limit 1;

